import React from 'react';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';

import IconButton from '@material-ui/core/IconButton'
import EditIcon from '@material-ui/icons/Edit';
import DoneIcon from '@material-ui/icons/Done';
import DeleteIcon from '@material-ui/icons/Delete';

class TodoItem extends React.Component {

    constructor(props) {
        super(props);
        this.state = {class: ""}
    }
/*
    handleChange = (i) => {
        if(i){
            this.setState({class: "StruckLine"});
        } else {
            this.setState({class: ""});
        }
    }
*/
    render (){

        const { item, deleteItem, doneItem } = this.props;

        return(
            <ListItem>
                
                <ListItemText className={this.state.class} primary={item.task} />
                
                <IconButton edge="end" onClick={ () => doneItem(item.id)}/* onChange={this.handleChange(item.done)}*/  >
                    <DoneIcon />
                </IconButton>
{/*
                <IconButton edge="end">
                    <EditIcon />
                </IconButton>
*/}
                <IconButton edge="end" onClick={() => deleteItem(item.id)}>
                    <DeleteIcon color="secondary" />
                </IconButton>

            </ListItem>
        );

    }
    
}

export default TodoItem;
