import React from 'react';
import './App.css';
import TodoList from './components/TodoList';
import Titles from './components/Titles';
import AddTodoItem from './components/AddTodoItem';

const staticList = [
    {id: 1, task: '1', done: true},
    {id: 2, task: '2', done: false},
    {id: 3, task: '3', done: true},
    {id: 4, task: '4', done: false},
];

class App extends React.Component {
    
    state={
        list: [],
    }

    newId=5;

    componentDidMount(){
       this.setState({list: staticList})
    }

    /* ADICIONAR ITEM
     * 
     */
    addItem = (newItem) => {
        newId =+ 5;
        alert("ID: " + newId, "Task: " + newItem);
        /*const item = {id: newId, task: newItem, done: false};
        staticList.push(item);
        this.setState({list: staticList});*/
    }

    /* DELETAR ITEM
     * Cria um novo array, igual ao principal, deleta o item com o id passado
     */
    deleteItem = (id) =>{
        const newList = [...this.state.list].map( l => Object.assign({}, l)).filter( l => l.id !== id)
        this.setState({list: newList});
    }

    render() {

        return(
            <div className="ToDo_List">

                <Titles title="To-Do List" size={34}/>

                <Titles title="Tarefas" size={24}/>
                
                <TodoList staticList={this.state.list} deleteItem={this.deleteItem}/>

                <Titles title="Adicionar tarefa" size={24}/>

                <AddTodoItem addItem={this.addItem}/>

            </div>
        );
        
    }
    
}

export default App;
