import React from 'react';
import SaveIcon from '@material-ui/icons/Save';
import TextField from '@material-ui/core/TextField';
import IconButton from '@material-ui/core/IconButton'

class AddTodoItem extends React.Component {

    constructor(props){
        
        super(props);
        this.state = {
            task: "",
        };

    }

    clear = () => {
        this.refs.textfield.value = "";
        this.setState({
            task: ""
        })
    }

    handleChange = (e) => {
        this.setState({
            task: e.target.value
        });
    }

    render () {

        const { addItem } = this.props 

        return (
            <div className="Task">
                <TextField ref="textfield" placeholder="Nova tarefa" onChange={this.handleChange}/>
                <IconButton onClick={() => {addItem(this.state.task); this.clear()}} >
                    <SaveIcon />
                </IconButton>
            </div>
        );

    }

}

export default AddTodoItem;
